package demo;

public class assignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=10;
		n1 +=5;
		System.out.println("The Output of n1 +=5 is " +n1);
		
		n1 -=5;
		System.out.println("The Output of n1 -=5 is " +n1);
		
		n1 *=5;
		System.out.println("The Output of n1 *=5 is " +n1);
		
		n1 /=5;
		System.out.println("The Output of n1 /=5 is " +n1);
		
		n1 %=5;
		System.out.println("The Output of n1 %=5 is " +n1);

	}

}
