package demo;

public class bitwiseOpertaor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=10, n2=5;
		System.out.println(n1&n2);
		System.out.println(n1|n2);
		System.out.println(n1^n2);
		System.out.println(~n1);
		System.out.println(~n2);
		

	}

}
