package sample;

public class bitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10, num2=5;
		System.out.println(num1&num2);
		System.out.println(num1|num2);
		System.out.println(num1^num2);
		System.out.println(~num1);		
	}

}
